mapper.select:
SELECT h.homework_no homeworkNo ,h.member_id teacherId ,hs.member_id studentId ,h.lecture_no lectureNo ,h.homework_title homeworkTitle ,h.homework_content homeworkContent ,h.homework_start_date homeworkStartDate ,h.homework_end_date homeworkEndDate ,h.homework_create_date homeworkCreateDate ,h.homework_update_date homeworkUpdateDate ,hs.homework_submit_no homeworkSubmitNo FROM homework h LEFT JOIN homework_submit hs ON h.homework_no = hs.homework_no AND hs.member_id = #{studentId}
AND h.lecture_No = #{lectureNo}
AND hs.member_Id = #{stduentId}
AND h.member_Id = #{teacherId}
AND h.homework_title like concat('%', #{searchWord}, '%')
GROUP BY h.homework_no ORDER BY h.homework_create_date DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:

		SELECT LAST_INSERT_ID()

INSERT INTO homework(
	member_id
	,lecture_no
	,homework_title
	,homework_content
	,homework_start_date
	,homework_end_date
	,homework_create_date
	,homework_update_date 
)VALUES(			
	#{memberId}
	,#{lectureNo}
	,#{homeworkTitle}
	,#{homeworkContent}
	,#{homeworkStartDate}
	,#{homeworkEndDate}
	,NOW()
	,NOW()
)

mapper.update:
UPDATE homework SET
	homework_title = #{homeworkTitle}
	,homework_content = #{homeworkContent}
	,homework_start_date = #{homeworkStartDate}
	,homework_end_date = #{homeworkEndDate}
	,homework_update_date = NOW()
WHERE homework_no = #{homeworkNo}

mapper.select:
SELECT COUNT(*) FROM homework
AND lecture_No = #{lectureNo}
AND homework_title like concat('%', #{searchWord}, '%')

