mapper.select:
SELECT h.homework_no homeworkNo ,h.member_id teacherId ,hs.member_id studentId ,h.lecture_no lectureNo ,h.homework_title homeworkTitle ,h.homework_content homeworkContent ,h.homework_start_date homeworkStartDate ,h.homework_end_date homeworkEndDate ,h.homework_create_date homeworkCreateDate ,h.homework_update_date homeworkUpdateDate ,hs.homework_submit_no homeworkSubmitNo FROM homework h LEFT JOIN homework_submit hs ON h.homework_no = hs.homework_no UNION (SELECT h.homework_no homeworkNo ,h.member_id teacherId ,hs.member_id studentId ,h.lecture_no lectureNo ,h.homework_title homeworkTitle ,h.homework_content homeworkContent ,h.homework_start_date homeworkStartDate ,h.homework_end_date homeworkEndDate ,h.homework_create_date homeworkCreateDate ,h.homework_update_date homeworkUpdateDate ,hs.homework_submit_no homeworkSubmitNo FROM homework h RIGHT JOIN homework_submit hs ON h.homework_no = hs.homework_no
AND h.lecture_no = #{lectureNo}
AND hs.member_id = #{stduentId}
AND h.member_id = #{teacherId}
GROUP BY h.homework_no ORDER BY h.homework_no DESC LIMIT #{beginRow}, #{rowPerPage})

mapper.insert:

		SELECT LAST_INSERT_ID()

INSERT INTO homework(
	member_id
	,lecture_no
	,homework_title
	,homework_content
	,homework_start_date
	,homework_end_date
	,homework_create_date
	,homework_update_date 
)VALUES(			
	#{memberId}
	,#{lectureNo}
	,#{homeworkTitle}
	,#{homeworkContent}
	,#{homeworkStartDate}
	,#{homeworkEndDate}
	,NOW()
	,NOW()
)

mapper.update:
UPDATE homework SET
	homework_title = #{homeworkTitle}
	,homework_content = #{homeworkContent}
	,homework_start_date = #{homeworkStartDate}
	,homework_end_date = #{homeworkEndDate}
	,homework_update_date = NOW()
WHERE homework_no = #{homeworkNo}

mapper.select:
SELECT COUNT(*) 
FROM homework
WHERE lecture_no = #{lectureNo}

SELECT hs.homework_submit_no homeworkSubmitNo ,hs.homework_no homeworkNo ,hs.member_id memberId ,h.lecture_no lectureNo ,hs.homework_submit_title homeworkSubmitTitle
,hsf.homework_submit_file_name homeworkSubmitFileName
,hs.homework_submit_create_date homeworkSubmitCreateDate ,hs.homework_submit_update_date homeworkSubmitUpdateDate FROM homework_submit hs INNER JOIN homework h ON hs.homework_no = h.homework_no INNER JOIN student_lecture sl ON hs.member_id = sl.member_id INNER JOIN homework_submit_file hsf ON hs.homework_submit_no = hsf.homework_submit_no WHERE hs.homework_no = h.homework_no ORDER BY hs.homework_submit_no DESC LIMIT #{beginRow}, #{rowPerPage}

mapper.insert:

		SELECT LAST_INSERT_ID()

