mapper.insert:
INSERT INTO student (
	member_id
	, student_education
	, student_name
	, student_birth
	, student_gender
	, student_email 
	, address_detail 
) VALUES (
	#{memberId}
	, #{studentEducation}
	, #{studentName}
	, #{studentBirth}
	, #{studentGender}
	, #{studentEmail}
	, #{addressDetail}
)

INSERT INTO member(
	member_id memberId
	,member_pw memberPw
	,member_pw_update_date memberPwUpdateDate
	,member_phone_no memberPhoneNo
	,member_level memberLevel
	,member_last_login_date memberLastLoginDate
	,member_create_date memberCreateDate
	,member_update_date memberUpdateDate
) VALUES (
	#{memberId}
	,#{memberPw}
	,#{memberPwUpdateDate}
	,#{memberPhoneNo}
	,#{memberLevel}
	,NOW()
	,NOW()
	,NOW()
)

